		function saveAddress(params, callback)
		{
			params[TOKEN_KEY] = TOKEN_VAL;
			
			$.ajax({
				type: 'POST',
				url:  '/Address/saveAddress',
				data: params,
				dataType: 'json',
				async: true,
				success: function(ajaxData)
				{					
					$('#error-warning-consignee').hide();
					$('#error-warning-pcd').hide();
					$('#error-warning-address').hide();
					$('#error-warning-zipcode').hide();
					$('#error-warning-mobile').hide();
					
					var ajaxData = (typeof ajaxData == 'string') ? $.parseJSON(ajaxData) : ajaxData;
					
					//console.log(ajaxData);
					
					if(ajaxData.data && ajaxData.code == 0)
					{			
						var resData = ajaxData.data;
						
						createAddress('#addressList', '.address-addNew', resData, params.state);
						callback && callback(resData.addressId);
					}
					else
					{
						switch (ajaxData.code)
						{
							case -10040:
							case -10041:
							case -10042:
								$('#error-warning-pcd').show().html(ajaxData.msg);
								break;
							
							case -10043:
							case -10044:
								$('#error-warning-address').show().html(ajaxData.msg);
								break;
							
							case -10045:
							case -10046:
								$('#error-warning-zipcode').show().html(ajaxData.msg);
								break;
							
							case -10047:
							case -10048:
								$('#error-warning-consignee').show().html(ajaxData.msg);
								break;
							
							case -10049:
							case -10050:
							case -10051:
								$('#error-warning-mobile').show().html(ajaxData.msg);
								break;
							
							case -10057:
								$.unblock(function(){
									
									saveAddressError(ajaxData.msg);
									
								});
								break;
							
							default:
								//console.log(ajaxData.msg);
								break;
						}
					}
				},
				error: function()
				{
					//console.log('error');
				}
			});
		}
		
		// 编辑收货地址
		function editAddress(aid)
		{
			$.ajax({
				type: 'POST',
				url: '/Address/editAddress',
				data: {
					aid: aid
				},
				dataType: 'json',
				success: function(ajaxData){
					
					var ajaxData = (typeof ajaxData == 'string') ? $.parseJSON(ajaxData) : ajaxData;
									
					if(ajaxData.code == 0)
					{
						var allTel = ajaxData.data.tel;
						var aTel = allTel.split('-');
						
						$('input[name=consignee]').val(ajaxData.data.consignee);
						$('input[name=province]').val(ajaxData.data.province);
						$('input[name=city]').val(ajaxData.data.city);
						$('input[name=district]').val(ajaxData.data.district);
						$('input[name=address]').val(ajaxData.data.address);
						$('input[name=zipcode]').val(ajaxData.data.zipcode);
						$('input[name=mobile]').val(ajaxData.data.mobile);
						$('input[name=code]').val(aTel[0]);
						$('input[name=tel]').val(aTel[1]);
						$('input[name=ext]').val(aTel[2]);
						
						if(ajaxData.data.province == 0)
						{
							$('#Province-select').remove();
						}
						
						if(ajaxData.data.city == 0)
						{
							$('#City-select').remove();
						}
						
						if(ajaxData.data.district == 0)
						{
							$('#District-select').remove();
						}
						
						linkage();
						
						if(ajaxData.data.status == 1)
						{
							$('input[name=state]').attr('checked', true).parent().removeClass('uncheckbox').addClass('checkbox');
						}
						else
						{
							$('input[name=state]').attr('checked', false).parent().removeClass('checkbox').addClass('uncheckbox');
						}
					}				
				}
			});
		}
		
